import './App.css';
import { useState } from "react"
import NavbarApp from "./components/NavbarApp"
import { Container, Row, Col, InputGroup, FormControl, Button, Image } from "react-bootstrap"

export default function App() {

  const [idMedecine, setIdMedecine] = useState(1)
  const [medecine, setMedecine] = useState({
    // id_mongo : 'mongo',
    // id_med: 'med',
    // cat: 'cat',
    // v : 'v'
  })

  function getDatas() {
    let url = `http://localhost:3010/medicine/${idMedecine}`

    fetch(url).then(function (res) {
      return res.json()
    }).then(function (datas) {
      // console.log(datas)
      setMedecine(datas)
    })

  }

  // function displayPics(){
  //   return pics.map(pic=>{

  //     return (
  //       <Col xs={6} md={4} className="m-3">
  //           <Image src={pic.download_url} width="200 " rounded />
  //       </Col>
  //     )
  //   })
  // }
  return (
    <div className="App">
      <NavbarApp />

      <Container >
        <Row className="mt-5">
          <Col md="6">
            <h1>Exemple d'appel API </h1>
            <hr />

            <InputGroup className="mb-3">
              <FormControl
                placeholder="Saisir un ID de medicament "
                value={idMedecine}
                onChange={(e) => { setIdMedecine(e.target.value) }}
              />
              <InputGroup.Text>Je ne sais pas quoi mettre pour le moment </InputGroup.Text>
            </InputGroup>

            <Button variant="info" onClick={getDatas}>Cliquez moi</Button>
          </Col>
        </Row>
      </Container>

      <Container>
        <Row>
          {
            <div className="col">
              <h1>Mi Casa</h1>
              <p>This is my house y&apos;all!</p>
              {medecine.map(home => <div>{home.name}</div>)}
            </div>
          }
        </Row>
      </Container>
    </div>
  );
}
